import { MoreVert, Share, ThumbDownOutlined, ThumbUpOutlined } from "@mui/icons-material";
import { Avatar, Button, Card, CardActions, CardContent, CardHeader, IconButton, styled, Typography } from "@mui/material";

const CardPost = styled(Card)(({theme}) => ({
    marginLeft: 'auto',
    marginRight: 'auto',

}));

const CardSimplePost = (props) => {
    const {post, name, date} = props;
    return (
        <CardPost sx={{maxWidth: 550}} >
            <CardHeader 
            avatar={<Avatar src="/broken-image.jpg" />}
            action={
                <IconButton aria-label="settings">
                  <MoreVert />
                </IconButton>
            }
            title={name}
            subheader={date} />
            <CardContent>
                <Typography variant='body1'> {post}</Typography>
            </CardContent>
            <CardActions>

                <Button size="small" startIcon={<ThumbUpOutlined />}>Upvote</Button>
                <Button size="small" startIcon={<ThumbDownOutlined />}>Downvote</Button>
                <Button size="small" startIcon={<Comm />}>Comment</Button>
                <Button size="small" startIcon={<Share />}>Share</Button>
            </CardActions>
        </CardPost>
    );
};

export default CardSimplePost;
