import { CommentOutlined, MoreVert, Share, ThumbDown, ThumbDownOutlined, ThumbUp, ThumbUpOutlined } from "@mui/icons-material";
import { Avatar, Box, Button, Card, CardActions, CardContent, CardHeader, Collapse, IconButton, styled, Typography } from "@mui/material";
import { useState } from "react";

const CardPost = styled(Card)(({ theme }) => ({
    marginLeft: 'auto',
    marginRight: 'auto',
    marginBottom: theme.spacing(2)
}));

const ResponsiveButton = styled(Button)(({ theme }) => ({
    [theme.breakpoints.down('md')]: {
        display: 'none'
    }
}));

const ResponsiveIconButton = styled(IconButton)(({ theme }) => ({
    [theme.breakpoints.up('md')]: {
        display: 'none'
    }
}));

const ExpandMore = styled((props) => {
    const { expand, ...other } = props;
    return <IconButton {...other} />;
})(({ theme, expand }) => ({
    transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
        duration: theme.transitions.duration.shortest,
    }),
}));

const CardSimplePost = (props) => {
    const { post, name, date } = props;
    const [upvote, setUpvote] = useState(false);
    const [downvote, setDownvote] = useState(false);
    const [expanded, setExpanded] = useState(false);

    const handleUpvoteReaction = () => {
        if (!upvote) {
            setUpvote(true);
            setDownvote(false);
        } else {
            setUpvote(false);
        }
    }

    const handleDownvoteReaction = () => {
        if (!downvote) {
            setUpvote(false);
            setDownvote(true);
        } else {
            setDownvote(false);
        }
    }

    const handleCommentClick = () => {

    }

    return (
        <CardPost sx={{ maxWidth: 550 }} >
            <CardHeader
                avatar={<Avatar src="/broken-image.jpg" />}
                action={
                    <IconButton aria-label="settings">
                        <MoreVert />
                    </IconButton>
                }
                title={name}
                subheader={date} />
            <CardContent>
                <Typography variant='body1'> {post}</Typography>
            </CardContent>
            <CardActions>
                <Box sx={{ flexGrow: 1 }} />
                <ResponsiveButton size="small"
                    startIcon={upvote ? <ThumbUp /> : <ThumbUpOutlined />}
                    onClick={handleUpvoteReaction} >
                    Upvote
                </ResponsiveButton>
                <ResponsiveIconButton><ThumbUpOutlined /></ResponsiveIconButton>
                <Box sx={{ flexGrow: 1 }} />
                <ResponsiveButton size="small"
                    startIcon={downvote ? <ThumbDown /> : <ThumbDownOutlined />}
                    onClick={handleDownvoteReaction}>
                    Downvote
                </ResponsiveButton>
                <ResponsiveIconButton
                    onClick={handleDownvoteReaction}>
                    {downvote ? <ThumbDown /> : <ThumbDownOutlined />}
                </ResponsiveIconButton>
                <Box sx={{ flexGrow: 1 }} />
                <ExpandMore
                    expand={expanded}
                    aria-expanded={expanded}
                    aria-label="show more">
                    <ResponsiveButton
                        size="small"
                        startIcon={<CommentOutlined />}
                        onClick={handleCommentClick}>Comment</ResponsiveButton>
                    <ResponsiveIconButton
                        onClick={handleCommentClick}>
                        <CommentOutlined />
                    </ResponsiveIconButton>

                </ExpandMore>

                <Box sx={{ flexGrow: 1 }} />
                <ResponsiveButton size="small" startIcon={<Share />}>Share</ResponsiveButton>
                <ResponsiveIconButton><Share /></ResponsiveIconButton>
                <Box sx={{ flexGrow: 1 }} />
            </CardActions>
            <Collapse in={expanded} timeout="auto" unmountOnExit>
                <CardContent>
                    <Typography paragraph>Method:</Typography>
                    <Typography paragraph>
                        Heat 1/2 cup of the broth in a pot until simmering, add saffron and set
                        aside for 10 minutes.
                    </Typography>
                    <Typography paragraph>
                        Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over
                        medium-high heat. Add chicken, shrimp and chorizo, and cook, stirring
                        occasionally until lightly browned, 6 to 8 minutes. Transfer shrimp to a
                        large plate and set aside, leaving chicken and chorizo in the pan. Add
                        pimentón, bay leaves, garlic, tomatoes, onion, salt and pepper, and cook,
                        stirring often until thickened and fragrant, about 10 minutes. Add
                        saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
                    </Typography>
                    <Typography paragraph>
                        Add rice and stir very gently to distribute. Top with artichokes and
                        peppers, and cook without stirring, until most of the liquid is absorbed,
                        15 to 18 minutes. Reduce heat to medium-low, add reserved shrimp and
                        mussels, tucking them down into the rice, and cook again without
                        stirring, until mussels have opened and rice is just tender, 5 to 7
                        minutes more. (Discard any mussels that don’t open.)
                    </Typography>
                    <Typography>
                        Set aside off of the heat to let rest for 10 minutes, and then serve.
                    </Typography>
                </CardContent>
            </Collapse>
        </CardPost >
    );
};

export default CardSimplePost;
