        setPosition(e.target.value);
    };

    const handleAddPosition = () => {
        if (position) {
            const obj = { 'key': keyVal, 'label': position };
            chipData.push(obj);
            setKeyVal(keyVal + 1);
            setPosition('');
        }
        console.log(chipData);
    };

    return (
        <CreatePollContainer>
