import { alpha, Dialog, DialogActions, DialogContent, DialogTitle, InputBase, styled, Typography } from "@mui/material";

const PostInput = styled(InputBase)(({theme}) => ({
    'label + &': {
        marginTop: theme.spacing(3),
      },
      '& .MuiInputBase-input': {

        position: 'relative',
        backgroundColor: theme.palette.mode === 'light' ? '#fcfcfb' : '#2b2b2b',
        border: '1px solid #ced4da',
        fontSize: 16,
        width: 'md',
        padding: '10px 12px',
        transition: theme.transitions.create([
          'border-color',
          'background-color',
          'box-shadow',
        ]),
        // Use the system font instead of the default Roboto font.
        fontFamily: [
          '-apple-system',
          'BlinkMacSystemFont',
          '"Segoe UI"',
          'Roboto',
          '"Helvetica Neue"',
          'Arial',
          'sans-serif',
          '"Apple Color Emoji"',
          '"Segoe UI Emoji"',
          '"Segoe UI Symbol"',
        ].join(','),
        '&:focus': {
          boxShadow: `${alpha(theme.palette.primary.main, 0.25)} 0 0 0 0.2rem`,
          borderColor: theme.palette.primary.main,
        },
      },
}));

const PostDialog = (props) => {
    const {open, onClose, post} = props;

    const handleClose = () => {
        onClose(post);
    };

    return (
        <Dialog open={open} onClose={handleClose} fullWidth maxWidth>
            <DialogTitle>
                <Typography variant="h6"> Create Post </Typography>
            </DialogTitle>
            <DialogContent>
                <PostInput />

            </DialogContent>
            <DialogActions>

            </DialogActions>
        </Dialog>
    );
};

export default PostDialog;
